import React, { useEffect, useState, useReducer } from 'react';
import axios from 'axios';

// const initialState = {

// }

// export const reducer = (state = initialState, action)=>{
//     switch(action.type) {

//     }
// }

export const contextApi = React.createContext();
export const DELETE_ITEM = 'DELETE_ITEM';

export const ContextApi = props =>{
    const [ article, setArticle ] = useState('');
    const [buttonClick, setButtonClick] = useState(false);
    const [inputValue, setInputValue] = useState('');
    const [content, setContent] = useState('');
    const contentRender = buttonClick === false ? [...article] : [content];

    const initialState = {
        contentRender : buttonClick === false ? [...article] : [content]
    }
    const reducer = (state = initialState, action)=>{
        switch(action.type) {
            case DELETE_ITEM:
                const { contentRender }  = state;
                console.log(contentRender);
                break;
            default:
                return null;
        }
    }
    console.log(initialState)
    const [mainContent, mainContentDispatch] = useReducer(reducer, initialState)
    console.log(mainContent, 'reducer')

    useEffect(() => {
        axios.get('http://hn.algolia.com/api/v1/search?query=').then((res) => {
          setArticle(res.data.hits);
        });
      }, []);
    const value = {
        article,
        setArticle,
        buttonClick,
        setButtonClick,
        inputValue,
        setInputValue,
        content,
        setContent,
        contentRender,
        mainContent,
        mainContentDispatch
    }
    return (
        <contextApi.Provider value={value}>
            {props.children}
        </contextApi.Provider>
    )
}
